    <script>
        document.addEventListener('DOMContentLoaded', function() {
        var submenuWrapper = document.querySelector('.submenu-wrapper');
        submenuWrapper.style.display = 'none'; // Ẩn submenu khi trang web được tải

        var mainmenu = document.querySelector('.mainmenu');
        var timer;

        mainmenu.addEventListener('mouseover', function() {
            clearTimeout(timer);
            submenuWrapper.style.display = 'block';
        });

        mainmenu.addEventListener('mouseleave', function() {
            timer = setTimeout(function() {
                submenuWrapper.style.display = 'none';
            }, 500);
            });
        });
    </script>

    <script>
        document.querySelectorAll('.quantity-select').forEach(function(select) {
            select.addEventListener('change', function() {
                var quantity = parseInt(this.value);
                var cartItemId = this.getAttribute('data-cart-item-id');
    
                localStorage.setItem('cartItem_' + cartItemId, quantity);
    
                // Lấy giá trị ban đầu của total-price, loại bỏ các ký tự không mong muốn trước khi chuyển đổi thành số
                var totalPriceElement = this.closest('.kl_lineItem').querySelector('.total-price');
                var originalPriceString = totalPriceElement.getAttribute('data-original-price');
                var originalPrice = parseFloat(originalPriceString);
    
                var newTotalPrice = quantity * originalPrice;
                
                // Giới hạn kết quả chỉ có 2 số sau dấu phẩy
                newTotalPrice = newTotalPrice.toFixed(2);
                
                totalPriceElement.textContent = newTotalPrice;
            });
        });
    
        document.addEventListener('DOMContentLoaded', function() {
            document.querySelectorAll('.quantity-select').forEach(function(select) {
                var cartItemId = select.getAttribute('data-cart-item-id');
                var storedQuantity = localStorage.getItem('cartItem_' + cartItemId);
    
                if (storedQuantity !== null) {
                    select.value = storedQuantity;
    
                    // Cập nhật tổng giá trên trang
                    var totalPriceElement = select.closest('.kl_lineItem').querySelector('.total-price');
                    var originalPriceString = totalPriceElement.getAttribute('data-original-price');
                    var originalPrice = parseFloat(originalPriceString);
    
                    var newTotalPrice = storedQuantity * originalPrice;
                    
                    // Giới hạn kết quả chỉ có 2 số sau dấu phẩy
                    newTotalPrice = newTotalPrice.toFixed(2);
    
                    totalPriceElement.textContent = newTotalPrice;
                }
            });
        });
    </script>
    
    <script>
        function updateTotalAmount() {
            var totalAmount = 0; // Khởi tạo biến totalAmount với giá trị ban đầu là 0
            var quantitySelects = document.querySelectorAll('.quantity-select');

            // Lặp qua mỗi lựa chọn số lượng và cộng vào tổng thành tiền
            quantitySelects.forEach(function(select) {
                var quantity = parseInt(select.value);
                var originalPriceString = select.closest('.kl_lineItem').querySelector('.total-price').getAttribute('data-original-price');
                var originalPrice = parseFloat(originalPriceString);
                totalAmount += quantity * originalPrice;
            });

            // Hiển thị tổng thành tiền trong phần tạm tính
            var totalAmountElement = document.getElementById('totalAmount');
            totalAmountElement.textContent = totalAmount.toFixed(2) + 'đ'; // Định dạng số thành tiền và hiển thị
        }
        document.addEventListener('DOMContentLoaded', function() {
        // Gọi hàm updateTotalAmount khi trang web được tải và khi số lượng sản phẩm thay đổi
        updateTotalAmount();

        var quantitySelects = document.querySelectorAll('.quantity-select');

        quantitySelects.forEach(function(select) {
            select.addEventListener('change', function() {
                updateTotalAmount();
                });
            });
        });
    </script>
</body>
